DROP VIEW IF EXISTS `figure_models_view`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `figure_models_view` AS select `hf`.`id` AS `figure_id`,`hf`.`name` AS `figure_name`,`hf`.`description` AS `figure_description`,`hf`.`avatar_url` AS `avatar_url`,`hf`.`dynasty` AS `dynasty`,`hf`.`birth_year` AS `birth_year`,`hf`.`death_year` AS `death_year`,`hf`.`profession` AS `profession`,`hf`.`achievements` AS `achievements`,`hfm`.`id` AS `model_id`,`hfm`.`name` AS `model_name`,`hfm`.`prompt` AS `prompt`,`hfm`.`model_type` AS `model_type`,`hfm`.`description` AS `model_description`,`hfm`.`is_active` AS `is_active` from (`historical_figures` `hf` left join `historical_figure_models` `hfm` on((`hf`.`id` = `hfm`.`figure_id`))) order by `hf`.`id`,`hfm`.`id` ;
